"use strict";

var fluid = fluid || require("infusion");

(function () {
    var gpii = gpii || fluid.registerNamespace("gpii");
    fluid.registerNamespace("gpii.canopyMatchMaker.utils");

    gpii.canopyMatchMaker.utils.computeFitness = function (leaves, solution) {
        var vector = [];
        for (var i = 0; i < leaves.length; ++ i) {
            var leaf = leaves[i];
            vector[i] = gpii.canopyMatchMaker.utils.prefixLength(leaf, solution);
        }
        vector = vector.sort(gpii.canopyMatchMaker.utils.sortDescending);
        return vector;
    };

    gpii.canopyMatchMaker.utils.compareFitness = function (solA, solB) {
        var i = 0, fitA = solA.fitness, fitB = solB.fitness;
        for (; ; ++i) {
            var endA = i === fitA.length, endB = i === fitB.length;
            if (endA || endB) {
                return endA && endB ? 0 : (endB ? -1 : 1);
            }
            var diff = fitB[i] - fitA[i];
            if (diff !== 0) {
                return diff;
            }
        }
    };

    gpii.canopyMatchMaker.utils.sortSolutions = function (solutions) {
        return solutions.sort(gpii.canopyMatchMaker.utils.compareFitness);
    };

    gpii.canopyMatchMaker.utils.rankSolutions = function (leaves, solrecs) {
        fluid.each(solrecs, function (solrec) {
            solrec.fitness = gpii.canopyMatchMaker.utils.computeFitness(leaves, solrec.skeleton);
        });
        solrecs = gpii.canopyMatchMaker.utils.sortSolutions(solrecs);
        return solrecs;
    };

    gpii.canopyMatchMaker.utils.disposeStrategy = function (leaves, solrecs) {
        var ranked = gpii.canopyMatchMaker.utils.rankSolutions(leaves, solrecs);
        var disposed = gpii.canopyMatchMaker.utils.disposeSolutionsImpl(leaves, ranked);
        return disposed;
    };

    gpii.canopyMatchMaker.utils.disposeSolutionsImpl = function (leaves, solrecs) {
        var canopy = fluid.transform(leaves, function () {
            return Number.NEGATIVE_INFINITY;
        });

        for (var i = 0; i < solrecs.length; ++ i) {
            var sol = solrecs[i];
            var inCanopy = false;
            for (var j = 0; j < leaves.length; ++ j) {
                var depth = gpii.canopyMatchMaker.utils.prefixLength(leaves[j], sol.skeleton);
                if (depth > canopy[j]) {
                    inCanopy = true;
                    canopy[j] = depth;
                }
            }
            if (inCanopy) {
                solrecs[i].disposition = "accept";
            }
            else {
                solrecs[i].disposition = "reject";
            }
        }
        return solrecs;
    };

    /** Returns a non-positive number indicating by how many path segments the supplied
     * path fails to index correctly into the supplied model. A return value of 0
     * indicates that the path indexes fully */
    gpii.canopyMatchMaker.utils.prefixLength = function (path, model) {
        var segs = fluid.pathUtil.parseEL(path);
        for (var i = 0; i < segs.length; ++ i) {
            var seg = segs[i];
            model = model[seg];
            if (model === undefined) {
                return i - segs.length;
            }
        }
        return 0;
    };

    // only used by canopyMatchMaker - moved from MatchMakerFramework
    gpii.canopyMatchMaker.utils.extractDispositions = function (solrecs) {
        var togo = [];
        fluid.each(solrecs, function (soln) {
            togo[soln.index] = soln.disposition;
        });
        return togo;
    };

    // only used by canopyMatchMaker - moved from MatchMakerFramework
    gpii.canopyMatchMaker.utils.sortDescending = function (numA, numB) {
        return numB - numA;
    };

    // TODO: UNUSED FUNCTION
    gpii.canopyMatchMaker.utils.makeComparator = function (leaves) {
        return function (solnA, solnB) {
            var domA = false, domB = false;
            for (var i = 0; i < leaves.length; ++ i) {
                var leaf = leaves[i];
                var goodA = gpii.canopyMatchMaker.utils.prefixLength(leaf, solnA.solution);
                var goodB = gpii.canopyMatchMaker.utils.prefixLength(leaf, solnB.solution);
                if (goodA > goodB) {
                    domA = true;
                }
                else if (goodB > goodA) {
                    domB = true;
                }
            }
            return domA ^ domB ? (domA ? 1 : -1) : 0;
        };
    };

    // TODO: UNNUSED FUNCTION
    gpii.canopyMatchMaker.utils.bestCoverage = function (leaves, soln) {
        var best = Number.NEGATIVE_INFINITY;
        for (var i = 0; i < leaves.length; ++ i) {
            var leaf = leaves[i];
            var good = gpii.canopyMatchMaker.utils.prefixLength(leaf, soln.solution);
            best = Math.max(good, best);
        }
        return best;
    };


})();
