"use strict";

var fluid = require("infusion");
var gpii = fluid.registerNamespace("gpii");

fluid.defaults("gpii.flowManager.getGpiiKey.handler", {
    gradeNames: ["kettle.request.http"],
    invokers: {
        handleRequest: {
            funcName: "gpii.flowManager.getGpiiKey.handleRequest",
            args: ["{that}", "{lifecycleManager}"]
        }
    }
});

gpii.flowManager.getGpiiKey.handleRequest = function (that, lifecycleManager) {
    var getGpiiKeyPromise = lifecycleManager.addToUserLogonRequestQueue({
        logonState: "getGpiiKey"
    });
    getGpiiKeyPromise.then(that.events.onSuccess.fire, that.events.onError.fire);
};
