"use strict";

var fluid = require("infusion"),
    gpii = fluid.registerNamespace("gpii");

fluid.defaults("gpii.ready.handler", {
    gradeNames: ["kettle.request.http"],
    invokers: {
        handleRequest: {
            funcName: "gpii.ready.handleRequest",
            args: ["{that}"]
        }
    }
});

gpii.ready.handleRequest = function (that) {
    that.events.onSuccess.fire();
};
