"use strict";

var fluid = require("infusion");
var gpii = fluid.registerNamespace("gpii");

// TODO: For now, follow the approach of the cloudBased Flow Manager
// of extending flowManager and disabling/overriding pieces. In the
// future, refactor flowManager so that the base is smaller and use
// composition rather than overriding.

// TODO: We now also have /user/:userToken/logonChange

fluid.defaults("gpii.flowManager.untrusted", {
    gradeNames: ["fluid.littleComponent", "autoInit"],
    components: {
        settingsDataSource: {
            type: "kettle.dataSource.URL",
            options: {
                url: "{gpii.flowManager}.options.urls.settings",
                termMap: {
                    "userToken": "%userToken",
                    "device": "%device"
                }
            }
        }
    },
    handlers: {
        userLogin: null,
        getUserToken: null,
        untrustedUserLogin: {
            route: "/user/:userToken/login",
            type: "get"
        }
    }
});

fluid.registerNamespace("gpii.request.flowManager.untrusted");

gpii.request.flowManager.untrusted.onUserLogin = function (handler, userToken) {
    fluid.log("Untrusted Flow Manager login userToken: " + userToken);
    handler.events.onUserToken.fire(userToken);
};

gpii.request.flowManager.untrusted.getSettings = function (settingsDataSource, userToken, deviceReporterData, event) {
    settingsDataSource.get({
        userToken: userToken,
        device: JSON.stringify(deviceReporterData)
    }, gpii.request.flowManager.logAndNotify("Fetched settings: ", event));
};

// TODO: Integrate with gpii.request.flowManager.userLogonStateChange.stateChangeHandler

fluid.defaults("kettle.requests.request.handler.untrustedUserLogin", {
    gradeNames: ["fluid.littleComponent", "autoInit"],
    invokers: {
        handle: {
            funcName: "gpii.request.flowManager.untrusted.onUserLogin",
            args: ["{that}", "{request}.req.params.userToken"]
        },
        getDeviceContext: {
            funcName: "gpii.request.flowManager.userLogonStateChange.getDeviceContext",
            args: ["{flowManager}.deviceReporterDataSource", "{that}.events.onDeviceContext", "{request}.events.onError"]
        },
        "getSettings": {
            funcName: "gpii.request.flowManager.untrusted.getSettings",
            args: [
                "{flowManager}.settingsDataSource",
                "{that}.userToken",
                "{arguments}.0", // device reporter data
                "{that}.events.onSettings"
            ]
        },
        startLifecycle: {
            funcName: "gpii.request.flowManager.userLogonStateChange.startLifecycle",
            args: [ "{flowManager}.lifecycleManager", "{arguments}.0", "{requestProxy}.events.onSuccess" ]
                // payload from Cloud-Based Flow Manager
        }
    },
    events: {
        onUserToken: null,
        onDeviceContext: null,
        onSettings: null
    },
    listeners: {
        onUserToken: [
            {
                priority: "first",
                listener: "gpii.request.flowManager.setUserToken",
                args: ["{that}", "{arguments}.0"]
            },
            "{that}.getDeviceContext"
        ],
        onDeviceContext: "{that}.getSettings",
        onSettings: "{that}.startLifecycle"
    }
});
