gpii.tests.preferencesServer.createPrefsSetSuccessSequence = [{
    func: "gpii.tests.dbOperation.invokePromiseProducer",
    args: ["{preferencesServer}.createPrefsSet", ["{gpii.tests.preferencesServer.environment}.options.preferences", "{gpii.tests.preferencesServer.environment}.options.view"], "{that}"]
}, {
    listener: "gpii.tests.preferencesServer.verifyCreatedPrefsSet",
    args: ["{gpii.tests.preferencesServer.environment}.options.preferences", "{arguments}.0"],
    event: "{that}.events.onResponse"
}];

gpii.tests.preferencesServer.verifyCreatedPrefsSet = function (preferences, response) {
    jqUnit.assertNotUndefined("The \"gpiiKey\" is returned", response.gpiiKey);
    jqUnit.assertDeepEq("The returned \"preferences\" value matches the input", preferences, fluid.get(response, ["preferences", "flat"]));
};

