"use strict";

var fluid = require("infusion");
fluid.registerNamespace("gpii");

fluid.defaults("kettle.requests.request.handler.processGet", {
    gradeNames: ["fluid.littleComponent", "autoInit"],
    invokers: {
        handle: {
            func: "{processReporter}.get",
            args: [ "{requestProxy}" ]
        }
    }
});

fluid.defaults("kettle.requests.request.handler.processGetSingle", {
    gradeNames: ["fluid.littleComponent", "autoInit"],
    invokers: {
        handle: {
            func: "{processReporter}.getProcess",
            args: [
                "{requestProxy}", "{request}.req.params.solutionID"
            ]
        }
    }
});
