(function () {

    "use strict";

    var fluid = require("infusion");
    fluid.registerNamespace("gpii");

    fluid.defaults("kettle.requests.request.handler.processGet", {
        gradeNames: ["fluid.littleComponent", "autoInit"],
        invokers: {
            handle: {
                func: "{processReporter}.get",
                args: [
                    "{requestProxy}"
                ]
            }
        }
    });

})();
