"use strict";

var fluid = require("infusion"),
    os = require("os"),
    path = require("path"),
    gpii = fluid.registerNamespace("gpii");

fluid.require("kettle", require);
fluid.require("./ProcessGet.js", require);
fluid.require("./processesBridge.js", require);

fluid.defaults("gpii.processReporter.base", {
    gradeNames: ["kettle.app", "autoInit"],
    handlers: {
        processGet: {
            route: "/processes",
            type: "get"
        },
        processGetSingle: {
            route: "/processes/:solutionID",
            type: "get"
        }
    },
    components: {
        platformReporter: {
            type: "gpii.platformReporter.native"
        },
        nameResolver: {
            type: "gpii.processReporter.nameResolver"
        }
    },
    invokers: {
        fireResponse: {
            funcName: "gpii.processReporter.fireResponse",
            args: ["{arguments}.0", "{arguments}.1", "{platformReporter}"]
        }
    }
});

fluid.defaults("gpii.processReporter.nameResolver", {
    gradeNames: ["fluid.littleComponent", "autoInit"],
    invokers: {
        resolveName: {
            funcName: "fluid.identity"
        }
    }
});

gpii.processReporter.fireResponse = function (requestProxy, installedSolutions, platformReporter) {
    requestProxy.events.onSuccess.fire({
        solutions: installedSolutions,
        OS: platformReporter.reportPlatform()
    });
};

fluid.defaults("gpii.processReporter.static", {
    gradeNames: ["autoInit", "gpii.processReporter.base"],
    runningSolutionsUrl: "",
    root: path.join(__dirname, ".."),
    components: {
        runningSolutionsDataSource: {
            type: "kettle.dataSource.URL",
            options: {
                url: "{gpii.processReporter.static}.options.runningSolutionsUrl"
            }
        }
    },
    invokers: {
        get: {
            funcName: "gpii.processReporter.static.get",
            args: ["{arguments}.0", "{processReporter}"]
        },
        getProcess: {
            funcName: "gpii.processReporter.static.getProcess",
            args: ["{arguments}.0", "{arguments}.1", "{processReporter}"]
        }
    }
});

gpii.processReporter["static"].get = function (requestProxy, processReporter) {
    var promise = processReporter.runningSolutionsDataSource.get();
    promise.then(function (runningSolutions) {
        if (!fluid.isDestroyed(processReporter)) { // Remove check when FLUID-5790 is resolved.
            fluid.each(runningSolutions, function (entry) {  // TODO:  find a better way.
                entry.running = true;
            });
            processReporter.fireResponse(requestProxy, runningSolutions);
        }
    }, function (err) {
        requestProxy.events.onError.fire({
            statusCode: 500,
            message: "Failed to read processReporter source. " + (err.message || err.error),
            isError: true
        });
    });
};

gpii.processReporter["static"].getProcess = function (requestProxy, solutionID, processReporter) {
    var promise = processReporter.runningSolutionsDataSource.get();
    promise.then(function (runningSolutions) {
        if (!fluid.isDestroyed(processReporter)) { // Remove check when FLUID-5790 is resolved.
            processReporter.fireResponse(requestProxy, { id: solutionID, running: true });
        }
    }, function (err) {
        requestProxy.events.onError.fire({
            statusCode: 500,
            message: "Failed to read processReporter source. " + (err.message || err.error),
            isError: true
        });
    });
};

fluid.defaults("gpii.processReporter.live", {
    gradeNames: ["autoInit", "gpii.processReporter.base"],
    invokers: {
        get: {
            funcName: "gpii.processReporter.live.get",
            args: ["{arguments}.0", "{processReporter}", "{flowManager}.solutionsRegistryDataSource"]
        },
        getProcess: {
            funcName: "gpii.processReporter.live.getProcess",
            args: ["{arguments}.0", "{arguments}.1", "{processReporter}", "{flowManager}.solutionsRegistryDataSource"]
        }
    }
});

gpii.processReporter.live.get = function (requestProxy, processReporter, solutionsRegistryDataSource) {
    var runningSolutions = [];
    solutionsRegistryDataSource.get({os: processReporter.platformReporter.reportPlatform().id}, function onSuccess(entries) {
        fluid.each(entries, function (entry, entryId) {
            if (!runningSolutions.some(function (s) { return s.id === entryId; })) {
                var foundEntry = fluid.find(entry.isRunning, function (runningSolutionsReporter) {
                    var resolvedName = processReporter.nameResolver.resolveName(runningSolutionsReporter.type, "processReporter");
                    var isRunning = fluid.invokeGradedFunction(resolvedName, runningSolutionsReporter);
                    return { id: entryId, running: isRunning };
                }, null);
                if  (foundEntry !== null) {
                    runningSolutions.push(foundEntry);
                }
            }
        });
        processReporter.fireResponse(requestProxy, runningSolutions);
    });
};

gpii.processReporter.live.getProcess = function (requestProxy, solutionID, processReporter, solutionsRegistryDataSource) {
    var result;
    solutionsRegistryDataSource.get({os: processReporter.platformReporter.reportPlatform().id}, function onSuccess(entries) {
        var solution = fluid.find(entries, function (anEntry, entryID) {
            if (entryID === solutionID) {
                return anEntry;
            }
        }, null);
        if (solution === null) {
            result = { id: solutionID, running: false };
        }
        else {
            result = fluid.find(solution.isRunning, function (runningSolutionsReporter) {
                var resolvedName = processReporter.nameResolver.resolveName(runningSolutionsReporter.type, "processReporter");
                var isRunning = fluid.invokeGradedFunction(resolvedName, runningSolutionsReporter);
                return { id: solutionID, running: isRunning };
            }, {id: solutionID, running: false});
        }
        processReporter.fireResponse(requestProxy, result);
    });
};

fluid.defaults("gpii.platformReporter.native", {
    gradeNames: ["fluid.littleComponent", "autoInit"],
    invokers: {
        reportPlatform: {
            funcName: "gpii.platformReporter.native.reportPlatform"
        }
    }
});

gpii.platformReporter["native"].reportPlatform = function () { // "native" is a reserved word
    return {
        // TODO: need to report more details - windowmanager, etc.
        id: os.platform(),
        // TODO: Need a better strategy - Node semver fails horribly
        // in the face of the benign underscore (eg. x86_64).
        version: os.release().replace("_", "-")
    };
};

fluid.defaults("gpii.processReporter.dev", {
    gradeNames: ["autoInit", "kettle.urlExpander.distributeDevVariables"]
});
