"use strict";

var fluid = require("infusion");

var gpii = fluid.registerNamespace("gpii");

fluid.registerNamespace("gpii.userListeners");

fluid.defaults("gpii.pcscUserListener", {
    gradeNames: ["fluid.component", "fluid.contextAware", "gpii.userListener"],
    contextAwareness: {
        platform: {
            checks: {
                windows: {
                    contextValue: "{gpii.contexts.windows}",
                    gradeNames: "gpii.pcscUserListener.windows"
                },
                linux: {
                    contextValue: "{gpii.contexts.linux}",
                    gradeNames: "gpii.pcscUserListener.linux"
                }
            }
        }
    },

    invokers: {
        startListener: {
            funcName: "gpii.userListeners.pcscStartListener",
            args: ["{that}"]
        },
        getPCSC: "fluid.identity"
    }
});


/**
 * Start the user listener.
 *
 * @param that {Component} The gpii.pcscUserListener instance.
 */
gpii.userListeners.pcscStartListener = function (that) {
    var pcsc = that.getPCSC();
    if (pcsc) {
        pcsc.on("reader", function (reader) {
            fluid.log("Got card reader", reader.name);
            reader.on("status", function (status) {
                gpii.userListeners.pcscStatusChanged(that, reader, status.state, status.atr);
            });
        });
        pcsc.on("error", function (err) {
            console.log("PCSC error", err);
        });

    } else {
        fluid.log("PC/SC user listener is not available.");
    }
};

/**
 * A card reader status has changed (the card has been added or removed).
 *
 * @param that {Component} The gpii.pcscUserListener instance.
 * @param reader {CardReader} The card reader.
 * @param state {number} The new reader state bit-mask.
 * @param atr {Buffer} The card's ATR.
 */
gpii.userListeners.pcscStatusChanged = function (that, reader, state, atr) {
    // Get the states that have changed to on.
    var newStates = (reader.state ^ state) & state;

    if (newStates & reader.SCARD_STATE_EMPTY) {
        fluid.log("SCARD: removed");
    } else if (newStates & reader.SCARD_STATE_PRESENT) {
        fluid.log("SCARD: added");
        gpii.userListeners.pcscReadCard(that, reader, atr);
    }
};

/**
 * Reads a card.
 *
 * @param that {Component} The gpii.pcscUserListener instance.
 * @param reader {CardReader} The card reader.
 * @param atr {Buffer} The card's ATR.
 */
gpii.userListeners.pcscReadCard = function (that, reader, atr) {
    console.log("read card", atr);
};
