findPrefsSafeByGpiiKey.map:
function(doc) {
  if (doc.type === 'gpiiKey') {
var record = { gpiiKey: doc};
if (doc.prefsSafeId) { record._id = doc.prefsSafeId; };
emit(doc._id, record);
}
}

findClientByOauth2ClientId.map:
function(doc) {
 if (doc.type === 'clientCredential' && doc.revoked === false) {
 var record = { clientCredential: doc };
 if (doc.clientId) {
 record._id = doc.clientId;
 }
 emit(doc.oauth2ClientId, record);
} 
}

findClientCredentialByClientId.map:
function(doc) {
 if (doc.type === 'clientCredential' && doc.revoked === false) {
 emit(doc.clientId, doc); 
}}

findAuthorizationByAccessToken.map:
function(doc) {
 if (doc.type === 'gpiiAppInstallationAuthorization' && doc.revoked === false) {
 emit(doc.accessToken, { authorization: doc });
 }
 }
